mapper.select:
SELECT *
FROM routine
WHERE user_id = #{userId} and day = #{day}

mapper.update:
UPDATE routine
SET type = #{type}, goal_amount = #{goalAmount}, unit = #{unit}
WHERE log_id = #{logId}

UPDATE routine
SET achieve_amount = #{achieveAmount}
WHERE log_id = #{logId}

mapper.select:
SELECT *
FROM routine
WHERE day BETWEEN #{startDate} AND #{endDate}
AND userId = #{userId}
